<?php//接口要求返回的字符串需要是utf8编码。//加载SDKnamespace app\index\controller;use CallbackSDK;use http\Params;use think\Controller;use think\Exception;use think\facade\Request;//设置app_key对应的app_secretheader( 'Content-type: text/html; charset=utf-8' );//初始化SDKclass Callback extends Controller{    public function index(){        $call_back_SDK = new CallbackSDK();        //签名验证        $signature = $_GET["signature"];        $timestamp = $_GET["timestamp"];        $nonce = $_GET["nonce"];        if (!$call_back_SDK->checkSignature($signature, $timestamp, $nonce)) {            die("check signature error");        }        //首次验证url时会有'echostr'参数，后续推送消息时不再有'echostr'字段        //若存在'echostr'说明是首次验证,则返回'echostr'的内容。        if (isset($_GET["echostr"])) {            die($_GET["echostr"]);        }        //处理开放平台推送来的消息,首先获取推送来的数据.        $post_msg_str = $call_back_SDK->getPostMsgStr();        /**         * 设置接口默认返回值为空字符串。         * 请注意数据编码类型。接口要求返回的字符串需要是utf8编码         * 需要说明的是开放平台判断推送成功的标志是接口返回的http状态码,         * 只要应用的接口返回的状态为200就会认为消息推送成功，如果http状态码不为200则会重试，共重试3次。         */        $str_return = '';        if (!empty($post_msg_str)) {            //sender_id为发送回复消息的uid，即蓝v自己            $sender_id = $post_msg_str['receiver_id'];            //receiver_id为接收回复消息的uid，即蓝v的粉丝            $receiver_id = $post_msg_str['sender_id'];            //回复text类型的消息示例。            $data_type = "text";            $data = $call_back_SDK->textData("text消息回复测试");            //回复articles类型的消息示例。            //    $data_type = "articles";            //    $article_data = array(            //        array("display_name" => "第一个故事",            //            "summary" => "今天讲两个故事，分享给你。谁是公司？谁又是中国人？",            //            "image" => "http://storage.mcp.weibo.cn/0JlIv.jpg",            //            "url" => "http://e.weibo.com/mediaprofile/article/detail?uid=1722052204&aid=983319"),            //        array("display_name" => "第二个故事",            //            "summary" => "今天讲两个故事，分享给你。谁是公司？谁又是中国人？",            //            "image" => "http://storage.mcp.weibo.cn/0JlIv.jpg",            //            "url" => "http://e.weibo.com/mediaprofile/article/detail?uid=1722052204&aid=983319")            //    );            //    $data = $call_back_SDK->articleData($article_data);            //回复position类型的消息示例。            //    $data_type = "position";            //    $longitude = "123.01";            //    $latitude = "154.2";            //    $data = $call_back_SDK->positionData($longitude, $latitude);            $str_return = $call_back_SDK->buildReplyMsg($receiver_id, $sender_id, $data, $data_type);        }        echo json_encode($str_return);    }}